<?php

/**
 * tbl_paymentTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class tbl_paymentTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object tbl_paymentTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('tbl_payment');
    }

    public function findPaymentByUser($nIdUser)
    {
        $q = $this->createQuery('p');
        $q->andWhere('p.id_user = ?', $nIdUser)
          ->andWhere('p.is_payed = ?', false)
          ->andWhere('p.is_brouillon = true');

        return $q->execute();
    }

    public function findPaymentByLigue($nIdLigue, $bSaisie=false)
    {
        $q = $this->createQuery('p');
        $q->leftJoin('p.tbl_club c')
          ->leftJoin('c.tbl_ligue l')
          ->andWhere('l.id = ?', $nIdLigue)
          ->andWhere('p.is_payed = ?', false);
        if ($bSaisie) {
          $q->andWhere('p.is_brouillon = true');
        }
        return $q->execute();
    }
    public function findPaymentClub($nIdClub, $bSaisie=false)
    {
        $q = $this->createQuery('p');
        $q->andWhere('p.id_club = ?', $nIdClub)
          ->andWhere('p.is_payed = ?', false);
        if ($bSaisie) {
          $q->andWhere('p.is_brouillon = true');
        } else {
          $q->andWhere('p.is_brouillon = false');
        }
        return $q->execute();
    }

    public function getAmountByLigue($nIdLigue)
    {
        $q = Doctrine_Query::create()
          ->select('SUM(p.amount) AS AmountTotal')
          ->from('tbl_payment p')
          ->leftJoin('p.tbl_club c')
          ->leftJoin('c.tbl_ligue l')
          ->andWhere('l.id = ?', $nIdLigue)
          ->andWhere('p.is_payed = ?', false);
        $result = $q->fetchOne();
        return $result['AmountTotal'];
    }

    public function getAmountClub($nIdClub)
    {
        $q = Doctrine_Query::create()
          ->select('SUM(p.amount) AS AmountTotal')
          ->from('tbl_payment p')
          ->andWhere('p.id_club = ?', $nIdClub)
          ->andWhere('p.is_brouillon = ?', false)
          ->andWhere('p.is_payed = ?', false);
        $result = $q->fetchOne();
        return $result['AmountTotal'];
    }

    public function validSaisie($nIdClub, $nIdUser)
    {
        $q = $this->createQuery('p');
        $q->where('p.id_club = ?', $nIdClub)
          ->andWhere('p.id_user = ?', $nIdUser)
          ->andWhere('p.is_brouillon = true');

        $oPaiements = $q->execute();
        foreach ($oPaiements as $oPaiement)
        {
          $oPaiement->setIsBrouillon(false)->save();
        }
    }

    public function cancelSaisie($nIdClub, $nIdUser)
    {
        $q = $this->createQuery('p');
        $q->where('p.id_club = ?', $nIdClub)
          ->andWhere('p.id_user = ?', $nIdUser)
          ->andWhere('p.is_brouillon = true');

        $oPaiements = $q->execute();
        foreach ($oPaiements as $oPaiement)
        {
          $oPaiement->delete();
        }
    }

    public function validSaisieByUser($nIdUser)
    {
        $q = $this->createQuery('p');
        $q->andWhere('p.id_user = ?', $nIdUser)
          ->andWhere('p.is_brouillon = true');

        $oPaiements = $q->execute();
        foreach ($oPaiements as $oPaiement)
        {
          $oPaiement->setIsBrouillon(false)->save();
        }
    }

    public function cancelSaisieByUser($nIdUser)
    {
        $q = $this->createQuery('p');
        $q->andWhere('p.id_user = ?', $nIdUser)
          ->andWhere('p.is_brouillon = true');

        $oPaiements = $q->execute();
        foreach ($oPaiements as $oPaiement)
        {
          $oPaiement->delete();
        }
    }
    public function retrieveByValide(Doctrine_Query $q) {
        $q = $this->createQuery('q');
        $q->andWhere('is_brouillon = false');

        return $q;
    }
}